import axios from 'axios';

const API = {
    Search:function(subject, start, end) {

        const APIKEY = "Tvaz9WY8OuEmCEkHz46Yf1xtbLLEXawn";
        let result = axios.get("https://api.nytimes.com/svc/search/v2/articlesearch.json?api-key=" + APIKEY + "&q=" + subject + "&begin_date=" + start + "0101&end_date=" + end + "0101");
        console.log(result);
        return result;
    },
    getArticles: function () {
        return axios.get('http://localhost:3001/api/saved');
    },
    saveArticle: function (article) {
        return axios.post('http://localhost:3001/api/save', article);
    },
    deleteArticle: function (id) {
        return axios.delete('http://localhost:3001/api/delete/' + id);
    }
}

export default API;
