import React, { Component } from 'react';
import SearchAndResult from '../SearchAndResult';
import SavedArticles from '../SavedArticles';
import Jumbotron from '../Jumbotron';
import API from '../../API';

class MainResult extends Component {
    state = {
        subject: "",
        startYear: "",
        endYear: "",
        articles: [],
        saved_articles:[]
    }
    componentDidMount() {
        console.log(this.state.saved_articles)
        this.getArticles();
    }
    getArticles = () => {
        API.getArticles()
            .then(res => { console.log(res); this.setState({ saved_articles: res.data }) })
            .catch(err=>console.log(err));
    }
    changeInputHandler = event => {
        const { name, value } = event.target;
        this.setState({
            [name]: value
        })
    }
    submitFormHandler = event => {
        event.preventDefault();
        console.log(this.state.subject);
        if (!this.state.subject || !this.state.endYear || !this.state.startYear) {
            alert("Fill out all the fields first");
            return;
        }
        API.Search(this.state.subject, this.state.startYear, this.state.endYear)
            .then(res => { this.setState({ articles: res.data.response.docs }) })
            .catch(err => console.log(err));
    }

    handleSaveEvent = (id) => {
        const article = this.state.articles.find(article => article._id === id);
        const article_to_save = {
            title: article.headline.main,
            date: article.pub_date,
            url: article.web_url
        };
        API.saveArticle(article_to_save)
            .then(this.getArticles)
            .catch(err => console.log(err));
    }

    handleDeleteEvent = (id) => {
        API.deleteArticle(id)
            .then(this.getArticles)
            .catch(err => console.log(err));
    };

    render(){
        return (
            <div>
                <Jumbotron />
                <div className="container">
                    <SearchAndResult 
                        changeInputHandler={this.changeInputHandler}
                        submitFormHandler={this.submitFormHandler}
                        handleSaveButton = {this.handleSaveEvent}
                        articles={this.state.articles}
                    />
                    <br></br><br></br><hr></hr>
                    <SavedArticles 
                        articles={this.state.saved_articles}

                        handleDeleteEvent = {this.handleDeleteEvent}
                        />
                </div>
            </div>
        )
    }
};

export default MainResult;
