import React, { Component } from 'react';
import SearchAndResult from '../SearchAndResult';
import Jumbotron from '../Jumbotron';
import API from '../../API';
import SavedBooks from '../SavedBooks';
import socketIOClient from "socket.io-client";

class MainResult extends Component {
    state = {
        subject: "",
        books:[],
        saved_books: [],
        response: [],
      endpoint: "http://127.0.0.1:3001"
    }
    componentDidMount() {
        this.getBooks();
        const { endpoint } = this.state;
        const socket = socketIOClient(endpoint);
        socket.on("FromAPI", data => this.setState({ response: data }));
    }
    getBooks = () => {
        API.getBooks()
            .then(res => { this.setState({ saved_books: res.data }) })
            .catch(err=>console.log(err));
    }
    changeInputHandler = event => {
        const { name, value } = event.target;
        this.setState({
            [name]: value
        })
    }
    submitFormHandler = event => {
        event.preventDefault();
        console.log(this.state.subject);
        if (!this.state.subject) {
            alert("Fill out all the fields first");
            return;
        }
        API.Search(this.state.subject)
            .then(res => { console.log(res);this.setState({ books: res.data.items }) })
            .catch(err => console.log(err));
    }

    handleSaveEvent = (id) => {
        const book = this.state.books.find(book => book.id === id);
        const book_to_save = {
            title: book.volumeInfo.title,
            date: book.volumeInfo.publishedDate,
            url: book.volumeInfo.previewLink,
            desc:book.volumeInfo.description.substring(0,255)
        };
        console.log(book_to_save);
        API.saveBooks(book_to_save)
            .then(this.getBooks)
            .catch(err => console.log(err));
    }

    handleDeleteEvent = (id) => {
        API.deleteBook(id)
            .then(this.getBooks)
            .catch(err => console.log(err));
    };

    render() {
        const { response } = this.state;
        alert(response);
        return (

            <div>
                <Jumbotron />
                <div className="container">
                        <div className="col-sm-8 col-md-8 col-lg-8">
                        <SearchAndResult 
                            changeInputHandler={this.changeInputHandler}
                            submitFormHandler={this.submitFormHandler}
                            handleSaveButton = {this.handleSaveEvent}
                            books={this.state.books}
                        />
                        <br></br><br></br><hr></hr>
                        <SavedBooks 
                            books={this.state.saved_books}
                            handleDeleteEvent = {this.handleDeleteEvent}
                            />
                    </div>
                    <div className="col-sm-4 col-md-4 col-lg-4">
                        <ul className="list-group">
                            {response.length >0 ?
                                this.state.response.forEach(res =>{
                                    <li className="list-group-item">{res.volumeInfo.title}</li>
                                }) 
                                : <li className="list-group-item">loading...</li>}
                        </ul>
                    </div>
                </div>
            </div>
        )
    }
};

export default MainResult;
